/* background tiling image */  

body {
/* perfect pentagon */

background-image: url(data:image/png;base64,) var(--bg) !important;

/* irregular pseudo-pixel*/
/*
background-image: url(data:image/png;base64,) var(--bg) !important;
*/

background-clip: border-box var(--bg) !important;
background-origin: padding-box var(--bg) !important;
background-attachment: scroll var(--bg) !important;
background-repeat: repeat var(--bg) !important;
background-size: auto var(--bg) !important;
background-position: left top var(--bg) !important;
}


/* general */

* {
border-color: var(--bdr) !important;
text-shadow: none !important;
}
a {
border-bottom: none;
color: var(--cft) !important;
}
a:hover {
color: var(--ft) !important;
}
strong {
color: var(--ft);
}
input {
color: var(--ft) !important;
}
textarea {
background-color: var(--bgd2) !important;
color: var(--ft) !important;
outline: 1px solid var(--bdr) !important;
}
select {
color: var(--ft) !important;
background-color: var(--bgd2) !important;
}
section {
background-color: var(--ctn1);
}
small {color: var(--ft);}body {
background-color: var(--ctn1) !important;
color: var(--ft);
}

h1, h2, h3, h4, h5, h6 {
color: var(--ft);
}
h1 {
font-size: 32px;
}
h2 {
font-size: 24px;
}
h3 {
font-size: 18px;
}
hr {
background-color: var(--bdr);
border-bottom: 1px solid var(--bdr);
border-right: 1px solid var(--bdr);
}
:root {
background-color: transparent;
}
th {
background-color: var(--btn) !important;
color: var(--ft) !important;
}
tr {
background-color: var(--ctn2) !important;
color: var(--ft) !important;
}
tr:hover {
background-color: var(--btn) !important;
}
li:nth-child(2n+1) {
background-color: transparent !important;
}
button {
background: var(--btn);
color: var(--ft);
}
::-moz-selection {
background: rgba(100,100,100,0.5) !important;
color: var(--bft) !important;
}
::selection {
background: rgba(100,100,100,0.5) !important;
color: var(--bft) !important;
}
::-webkit-input-placeholder {
color: var(--dft) !important;
}
::-moz-placeholder {
color: var(--dft) !important;
opacity: 1;
}

/* bootstrap */
  .button.disabled {background-color: var(--btn);}.jumbotron, .entry {
background-color: var(--ctn2);
}
.link-url:hover {
color: var(--ft) !important;
}
.link-url {
color: var(--cft) !important;
}
.menu {
background-color: var(--bgd2) !important;
}
.menu-item:hover {
background-color: var(--ctn2) !important;
}
.menu-radio a {
background-color: var(--btn);
}
.select {
background-color: var(--bgd2);
}
.btn {
background: var(--btn);
color: var(--ft);
}
.btn-default {
color: var(--dft);
background-color: var(--btn);
}
.btn-default:hover {
color: var(--dft);
background-color: var(--hbtn);
}
.btn-primary {
color: var(--dft);
background-color: var(--btn);
}
.btn-primary:hover {
color: var(--dft);
background-color: var(--hbtn);
}
.btn.focus, .btn:focus, .btn:hover {
color: var(--ft);
background-color: var(--hbtn);
}
.button, .button.is-info,
.button.is-link, .button.is-primary {
background-color: var(--btn);
color: var(--ft);
}
.button.is-link.is-focused, .button.is-link.is-hovered,
.button.is-link:focus, .button.is-link:hover,
.button.is-success.is-outlined:focus, .button.is-success.is-outlined:hover,
.button.is-hovered, .button:hover {
color: var(--ft);
background-color: var(--hbtn);
}
.button.is-success {
background-color: var(--hbtn) !important;
}
.input, .textarea {
background-color: var(--bgd2);
color: var(--ft);
}
.nav, .navbar, .navbar.is-white {
background-color: var(--ctn2);
}
.title {
color: var(--ft);
}
.subtitle {
color: var(--dft);
}
.box {
background: var(--ctn2) !important;
color: var(--ft);
box-shadow: none !important;
border: 1px solid var(--bdr) !important;
}
.content {
background: transparent;
}
.left {
background-color: transparent;
}
body.loaded .right {
background-color: var(--ctn1);
}
.right {
background-color: transparent;
}
.popup {
background-color: var(--ctn2);
}
.dropdown-content {
background-color: var(--bgd1);
}
a.dropdown-item:hover {
background-color: var(--btn);
}
.modal-card-foot, .modal-card-head {
background-color: var(--ctn2);
}
.modal-card-title {
color: var(--ft);
}
.modal-card-body {
background-color: var(--ctn2);
}
.label {
color: var(--ft);
}
.select select {
background-color: var(--bgd1);
color: var(--ft);
}
.footer {
background-color: var(--ctn1);
}
.table {
color: var(--dft);
}
.tag {
color: var(--ft);
}
.tag.is-success {
background-color: var(--hbtn);
color: var(--ft);
}
.tag.is-light {
color: var(--dft);
}

/* jsdiff */
.diff-header {
background-color: var(--ctn2);
border-bottom: 1px solid var(--bdr);
}
table.diff .skip {
background-color: var(--btn);
}
table.diff .delete {
background-color: #422;
}
table.diff .insert {
background-color: #242;
}
table.diff .replace {
background-color: #750;
}
table.diff .empty {
background-color: var(--btn);
}

/* notification */

.notification > .body > .message > div {
background-color: var(--hbtn) !important;
}
.notification .body .message div,
.notification .body .message small {
color: var(--ft) !important;
}
.notification .body {
background-color: var(--btn);
}
.notification {
color: var(--ft);
}
.notification .input {
background-color: var(--bgd2) !important;
}
.notification .select {
background-color: var(--btn);
}
.notification .select:hover, .notification .select.active {
background-color: var(--hbtn);
}

 /* Sidebar */.sidebar {background-color: var(--ctn1);}.globe.error {color: var(--ft) !important;}.sidebar .content,.sidebar .fields label small {color: var(--ft);}.sidebar .fields label {color: var(--cft);}.sidebar .fields .disabled {color: var(--dft);}.sidebar .fields .text {background-color: var(--bgd2);}.contents {background-color: var(--ctn2);color: var(--ft);} /* loading screen page */
.unsupported {
background-color: var(--ctn2);
box-shadow: 0px 0px 5px #888 var(--light);
}

.loadingscreen {
background-color: var(--ctn1);
}  

/* blacklist page */
.content #details {
background-color: var(--btn);
box-shadow: none;
}
.content {
color: var(--ft);
}

/* code theme */

pre {
color: var(--bft);
background-color: var(--btn); /* '!important' affects NullPaste selected text color */
border: 0px solid #060606 !important;
overflow: auto;
}
pre code {
color: var(--bft);
background-color: var(--btn) !important;
border: 0px solid #060606 !important;
}
code {
color: #FFA0A0 var(--light);
background-color: var(--btn) !important;
}
.hljs-tag, .hljs-name, .hljs-attribute {
color: #F0E68C var(--light);
}
.hljs-keyword, .hljs-selector-tag, .hljs-subst {
color: #F0E68C var(--light);
}
.hljs-built_in, .hljs-builtin-name {
color: #98FB98 var(--light);
}
.hljs {
color: var(--bft);
background-color: var(--btn) !important;
border: 0px solid #060606 !important;
}
.hljs-title, .hljs-section, .hljs-selector-id, .hljs-selector-class {
color: #98FB98 var(--light);
}
.hljs-meta {
color: #98FB98 var(--light);
}
.hljs-number, .hljs-literal, .hljs-variable, .hljs-template-variable, .hljs-tag .hljs-attr {
color: #FFA0A0 var(--light);
}
.hljs-string, .hljs-doctag {
color: #FFA0A0 var(--light);
}
.hljs-comment, .hljs-quote, .hljs-link {
color: #87CEEB var(--light);
}
.hljs-symbol, .hljs-bullet {
color: #FFA858 var(--light);
}
.hljs-tag .hljs-title, .hljs-rules .hljs-property, .django .hljs-tag .hljs-keyword {
color: #FFA0A0 var(--light);
}
.hljs-string, .hljs-tag .hljs-value, .hljs-phpdoc, .hljs-dartdoc, .tex .hljs-formula {
color: var(--bft);
}
.hljs-title, .hljs-id, .scss .hljs-preprocessor {
color: #F0E68C var(--light);
}
.hljs-number, .hljs-hexcolor, .ruby .hljs-constant {
color: #FFA0A0 var(--light);
}
.hljs-deletion {
background: #422 var(--light);
}
.hljs-addition {
background: #242 var(--light);
}
.hljs-winutils, .nginx .hljs-title, .hljs-subst, .hljs-request, .hljs-status {
color: var(--bft);
}

/* Debug console */
div.debugger {
background-color: var(--ctn2);
}  
div.traceback pre {
background-color: var(--btn);
color: var(--ft);
}
div.traceback pre:hover {
background-color: var(--hbtn);
color: var(--ft);
}
div.explanation {
color: var(--ft);
}
div.pin-prompt .inner {
background: var(--ctn2);
}
div.pin-prompt {
background: rgba(30, 30, 30, 0.8) var(--light);
}  
pre.console {
background: var(--btn) !important;
}
pre.console form, pre.console input {
color: var(--ft);
}


/* ZeroHello | ZeroTalk | ZeroBlog */
.menu-radio a:hover,
.menu-radio a.selected {
background-color: var(--hbtn);
