{{row of table of mathematical symbols
 | symbol   = {{math|[[plus sign|+]]}}
 | tex      = <math>+</math>
 | rowspan  = 2
 | name     = [[addition]]
 | readas   = [[plus and minus signs|plus]];<br/>add
 | category = [[arithmetic]]
 | explain  = {{math|4 + 6}} means the sum of {{math|4}} and {{math|6}}.
 | examples = {{math|2 + 7 {{=}} 9}}
}}

{{row of table of mathematical symbols
 | name     = [[disjoint union]]
 | readas   = the disjoint union of ... and ...
 | category = [[naive set theory|set theory]]
 | explain  = {{math|''A''<sub>1</sub> + ''A''<sub>2</sub>}} means the disjoint union of sets {{math|''A''<sub>1</sub>}} and {{math|''A''<sub>2</sub>}}.
 | examples = {{math|''A''<sub>1</sub> {{=}} {3, 4, 5, 6} ∧ ''A''<sub>2</sub> {{=}} {7, 8, 9, 10} ⇒<br/>''A''<sub>1</sub> + ''A''<sub>2</sub> {{=}} {(3, 1), (4, 1), (5, 1), (6, 1), (7, 2), (8, 2), (9, 2), (10, 2)<nowiki>}</nowiki>}}
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[minus sign|−]]}}
 | tex      = <math>-</math>
 | rowspan  = 3
 | name     = [[subtraction]]
 | readas   = [[plus and minus signs|minus]];<br/>take;<br/>subtract
 | category = [[arithmetic]]
 | explain  = {{math|36 − 11}} means the subtraction of {{math|11}} from {{math|36}}.
 | examples = {{math|36 − 11 {{=}} 25}}
}}

{{row of table of mathematical symbols
 | name     = [[plus and minus signs|negative sign]]
 | readas   = negative;<br/>minus;<br/>the opposite of
 | category = [[arithmetic]]
 | explain  = {{math|−3}} means the [[additive inverse]] of the number {{math|3}}.
 | examples = {{math|−(−5) {{=}} 5}}
}}

{{row of table of mathematical symbols
 | name     = [[complement (set theory)|set-theoretic complement]]
 | readas   = minus;<br/>without
 | category = [[naive set theory|set theory]]
 | explain  = {{math|''A'' − ''B''}} means the set that contains all the elements of {{math|''A''}} that are not in {{math|''B''}}.<br/><br/>({{math|∖}} ''can also be used for set-theoretic complement as described below.'')
 | examples = {{math|{1, 2, 4} − {1, 3, 4} {{=}} {2<nowiki>}</nowiki>}}
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[plus-minus sign|&plusmn;]]}}<br/><small></small>
 | tex      = <math>\pm</math><br/>\pm
 | rowspan  = 2
 | name     = [[plus-minus sign|plus-minus]]
 | readas   = plus or minus
 | category = [[arithmetic]]
 | explain  = {{math|6 &plusmn; 3}} means both {{math|6 + 3}} and {{math|6 − 3}}.
 | examples = The equation {{math|''x'' {{=}} 5 &plusmn; {{sqrt|4}}}}, has two solutions, {{math|''x'' {{=}} 7}} and {{math|''x'' {{=}} 3}}.
}}

{{row of table of mathematical symbols
 | name     = [[plus-minus sign|plus-minus]]
 | readas   = plus or minus
 | category = [[measurement]]
 | explain  = {{math|10 &plusmn; 2}} or equivalently {{math|10 &plusmn; 20%}} means the range from {{math|10 − 2}} to {{math|10 + 2}}.
 | examples = If {{math|''a'' {{=}} 100 &plusmn; 1 [[millimetre|mm]]}}, then {{math|''a'' &ge; 99 mm and ''a'' &le; 101 mm}}.
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[minus-plus sign|&#x2213;]]}}<br/><small></small>
 | tex      = <math>\mp</math><br/>\mp
 | rowspan  = 1
 | name     = [[Minus-plus sign#Minus-plus sign|minus-plus]]
 | readas   = minus or plus
 | category = [[arithmetic]]
 | explain  = {{math|6 &plusmn; (3 &#x2213; 5)}} means {{math|6 + (3 − 5)}} and {{math|6 − (3 + 5)}}.
 | examples = {{math|cos(''x'' &plusmn; ''y'') {{=}} cos(''x'') cos(''y'') &#x2213; sin(''x'') sin(''y'').}}
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[multiplication sign|×]]}}<br /><br />{{math|[[dot operator|&sdot;]]}}<br><br />{{math|[[middle dot|&middot;]]}}
 | tex      = <math>\times</math><br/>\times<br/><br/><math>\cdot</math><br/>\cdot
 | rowspan  = 4
 | name     = [[multiplication]]
 | readas   = times;<br/>multiplied by
 | category = [[arithmetic]]
 | explain  = {{math|3 &times; 4}} or {{math|3 &sdot; 4}} means the multiplication of {{math|3}} by {{math|4}}.
 | examples = {{math|7 &sdot; 8 {{=}} 56}}
}}

{{row of table of mathematical symbols
 | name     = [[dot product]] [[scalar product]]
 | readas   = dot
 | category = [[linear algebra]] [[vector algebra]]
 | explain  = {{math|'''u''' &sdot; '''v'''}} means the dot product of [[vector (geometry)|vector]]s {{math|'''u'''}} and {{math|'''v'''}}
 | examples = {{math|(1, 2, 5) &sdot; (3, 4, −1) {{=}} 6}}
}}

{{row of table of mathematical symbols
 | name     = [[cross product]] [[vector product]]
 | readas   = cross
 | category = [[linear algebra]] [[vector algebra]]
 | explain  = {{math|'''u''' &times; '''v'''}} means the cross product of [[vector (geometry)|vector]]s {{math|'''u'''}} and {{math|'''v'''}}
 | examples = {{math|(1, 2, 5) &times; (3, 4, −1) {{=}} <!--
-->{{aligned table|cols=3|col1width=20px|col2width=20px|col3width=20px
   |style=display: inline-table; vertical-align: middle; text-align: center; padding: 0px; margin: 0px; border-left: 1px solid black; border-right: 1px solid black
   |{{math|'''i'''}} | {{math|'''j'''}} | {{math|'''k'''}}
   |1 | 2 | 5
   |3 | 4 | −1
   }}<!--
-->{{=}} (−22, 16, −2)}}
}}

{{row of table of mathematical symbols
 | name     = placeholder
 | readas   = (silent)
 | category = [[functional analysis]]
 | explain  = A {{math|&middot;}} means a placeholder for an argument of a function. Indicates the functional nature of an expression without assigning a specific symbol for an argument.
 | examples = {{math|{{abs|}}}}
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[division sign|&divide;]]}}<br/><br/>{{math|[[fraction slash|&frasl;]]}}
 | tex      = <math>\div</math><br/>\div<br/><br/><math>/</math>
 | rowspan  = 3
 | name     = [[division (mathematics)|division]] ([[Obelus]])
 | readas   = divided by;<br/>over
 | category = [[arithmetic]]
 | explain  = {{math|6 &divide; 3}} or {{math|6 &frasl; 3}} means the division of {{math|6}} by {{math|3}}.
 | examples = {{math|2 &divide; 4 {{=}} 0.5}}<br/><br/>{{math|12 &frasl; 4 {{=}} 3}}
}}

{{row of table of mathematical symbols
 | name     = [[quotient group]]
 | readas   = mod
 | category = [[group theory]]
 | explain  = {{math|''G'' / ''H''}} means the quotient of group {{math|''G''}} [[Ideal (ring theory)|modulo]] its subgroup {{math|''H''}}.
 | examples = {{math|{0, ''a'', 2''a'', ''b'', ''b'' + ''a'', ''b'' + 2''a''} / {0, ''b''} {{=}} <nowiki>{{</nowiki>0, ''b''}, {''a'', ''b'' + ''a''}, {2''a'', ''b'' + 2''a''<nowiki>}}</nowiki>}}
}}

{{row of table of mathematical symbols
 | name     = quotient set
 | readas   = mod
 | category = [[set theory]]
 | explain  = {{math|''A''/~}} means the set of all {{math|~}} [[equivalence class]]es in {{math|''A''}}.
 | examples = If we define {{math|~}} by {{math|''x'' ~ ''y'' ⇔ ''x'' − ''y'' ∈ &#x2124;}}, then {{math|&#x211D;/~ {{=}} {''x'' + ''n'' : ''n'' ∈ &#x2124;, ''x'' ∈ [0,1)<nowiki>}</nowiki>}}.
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[radical symbol|√]]}}
 | tex      = <math>\surd</math><br/>\surd<br/><math>\sqrt{x}</math><br/>\sqrt{x}
 | rowspan  = 2
 | name     = [[square root]] ([[radical symbol]])
 | readas   = the (principal) square root of
 | category = [[real numbers]]
 | explain  = {{math|{{sqrt|''x''}}}} means the nonnegative number whose square is{{nbsp}}{{math|''x''}}.
 | examples = {{math|{{sqrt|4}} {{=}} 2}}
}}

{{row of table of mathematical symbols
 | name     = [[square root#Square roots of complex numbers|complex square root]]
 | readas   = the (complex) square root of
 | category = [[complex numbers]]
 | explain  = If {{math|''z'' {{=}} ''r'' exp(''i&phi;'')}} is represented in [[polar coordinate system|polar coordinate]]s with {{math|−''&pi;'' < ''&phi;'' &le; ''&pi;''}}, then {{math|{{sqrt|''z''}} {{=}} {{sqrt|''r''}} exp(''i&phi;''/2)}}.
 | examples = {{math|{{sqrt|−1}} {{=}} ''i''}}
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[Sigma (letter)|∑]]}}
 | tex      = <math>\sum</math><br/>\sum
 | rowspan  = 1
 | name     = [[summation]]
 | readas   = sum over ... from ... to ... of
 | category = [[calculus]]
 | explain  = <math>\sum_{k=1}^{n}{a_k}</math> means <math>a_1 + a_2 + \cdots + a_n</math>.
 | examples = <math>\sum_{k=1}^{4}{k^2} = 1^2 + 2^2 + 3^2 + 4^2 = 1 + 4 + 9 + 16 = 30</math>
}}

{{row of table of mathematical symbols
 | symbol   = {{math|''[[Integral symbol|{{intmath}}]]''}}
 | tex      = <math>\int</math><br/>\int
 | rowspan  = 3
 | name     = [[indefinite integral]] or [[antiderivative]]
 | readas   = indefinite integral of<br/>- OR -<br/>the antiderivative of
 | category = [[calculus]]

 | explain  = {{math|''{{intmath}}'' ''f''(''x'') ''dx''}} means a function whose derivative is {{mvar|f}}.
 | examples = <math>\int x^2 dx = \frac{x^3}3 +C</math>
}}

{{row of table of mathematical symbols
 | name     = [[definite integral]]
 | readas   = integral from ... to ... of ... with respect to
 | category = [[calculus]]
 | explain  = {{math|''{{intmath||a|b}}'' ''f''(''x'') ''dx''}} means the signed [[area]] between the {{mvar|x}}-axis and the [[graph (functions)|graph]] of the [[function (mathematics)|function]] {{mvar|f}} between {{math|''x'' {{=}} ''a''}} and {{math|''x'' {{=}} ''b''}}.
 | examples = {{math|''{{intmath||a|b}}'' ''x''<sup>2</sup> ''dx'' {{=}} {{sfrac|''b''<sup>3</sup> − ''a''<sup>3</sup>|3}}}}
}}

{{row of table of mathematical symbols
 | name     = [[line integral]]
 | readas   = line/ path/ curve/ integral of ... along ...
 | category = [[calculus]]
 | explain  = {{math|''{{intmath||C}}'' ''f'' ''ds''}} means the integral of {{mvar|f}} along the curve {{mvar|C}}, {{math|''{{intmath||a|b}}'' ''f''('''r'''(''t'')) {{abs|'''r'''<nowiki>'</nowiki>(''t'')}} ''dt''}}, where {{math|'''r'''}} is a parametrization of {{mvar|C}}. (If the curve is closed, the symbol {{math|''{{intmath|oint}}''}} may be used instead, as described below.)
 | examples =
}}

{{row of table of mathematical symbols
 | symbol   = {{math|''{{intmath|oint}}''}}
 | tex      = <math>\oint</math><br/>\oint
 | rowspan  = 1
 | name     = Contour integral;<br/>closed [[line integral]]
 | readas   = contour integral of
 | category = [[calculus]]
 | explain  = Similar to the integral, but used to denote a single integration over a closed curve or loop. It is sometimes used in physics texts involving equations regarding [[Gauss's Law]], and while these formulas involve a closed [[surface integral]], the representations describe only the first integration of the volume over the enclosing surface. Instances where the latter requires simultaneous double integration, the symbol {{math|''{{intmath|oiint}}''}} would be more appropriate.  A third related symbol is the closed [[volume integral]], denoted by the symbol {{math|''{{intmath|oiiint}}''}}.

The contour integral can also frequently be found with a subscript capital letter C, {{math|''{{intmath|oint}}''<sub>''C''</sub>}}, denoting that a closed loop integral is, in fact, around a contour {{mvar|C}}, or sometimes dually appropriately, a circle {{mvar|C}}. In representations of Gauss's Law, a subscript capital S, {{math|''{{intmath|oint}}''<sub>''S''</sub>}}, is used to denote that the integration is over a closed surface.
 | examples = If {{mvar|C}} is a [[Jordan curve]] about 0, then {{math|''{{intmath|oint}}''<sub>''C''</sub> {{sfrac|1|''z''}} ''dz'' {{=}} 2''&pi;i''}}.
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[ellipsis|…]]<br/><br/>⋯<br/><br/>⋮<br/><br/>⋰<br/><br/>⋱}}
 | tex      = <math>\ldots</math><br/>\ldots<br/><br/><math>\cdots</math><br/>\cdots<br/><br/><math>\vdots</math><br/>\vdots<br/><br/><math>\ddots</math><br/>\ddots
 | rowspan  = 1
 | name     = [[ellipsis]]
 | readas   = and so forth
 | category = everywhere
 | explain  = Indicates omitted values from a pattern.
 | examples = [[1/2 + 1/4 + 1/8 + 1/16 + ⋯]] = 1
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[therefore sign|∴]]}}
 | tex      = <math>\therefore</math><br/>\therefore
 | rowspan  = 1
 | name     = [[therefore]]
 | readas   = therefore;<br/>so;<br/>hence
 | category = everywhere
 | explain  = Sometimes used in proofs before [[logical consequence]]s.
 | examples = All humans are mortal. Socrates is a human. ∴ Socrates is mortal.
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[because sign|∵]]}}
 | tex      = <math>\because</math><br/>\because
 | rowspan  = 1
 | name     = [[wiktionary:because|because]]
 | readas   = because;<br/>since
 | category = everywhere
 | explain  = Sometimes used in proofs before reasoning.
 | examples = 11 is [[prime number|prime]] ∵ it has no positive integer factors other than itself and one.
}}

{{row of table of mathematical symbols
 | name     = [[factorial]]
 | symbol   = {{math|[[exclamation mark|<nowiki>!</nowiki>]]}}
 | tex      = <math>!</math>
 | rowspan  = 2
 | readas   = factorial
 | category = [[combinatorics]]
 | explain  = <math>n!</math> means the product <math>1 \times 2 \times \cdots \times n</math>.
 | examples = <math>4! = 1\times2\times3\times4 = 24</math>
}}

{{row of table of mathematical symbols
 | name     = [[logical negation]]
 | readas   = not
 | category = [[propositional logic]]
 | explain  = The statement !''A'' is true if and only if ''A'' is false.<br/><br/>A slash placed through another operator is the same as "!" placed in front.<br/><br/>(''The symbol'' ! ''is primarily from computer science. It is avoided in mathematical texts, where the notation'' ¬''A'' ''is preferred.'')
 | examples = !(!''A'') ⇔ ''A'' <br/>''x'' ≠ ''y''  ⇔  !(''x'' = ''y'')
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[not sign|¬]]}}<br/><br/>{{math|˜}}
 | tex      = <math>\neg</math><br/>\neg<br/><br/><math>\sim</math>
 | rowspan  = 1
 | name     = [[logical negation]]
 | readas   = not
 | category = [[propositional logic]]
 | explain  = The statement ¬''A'' is true if and only if ''A'' is false.<br/><br/>A slash placed through another operator is the same as "¬" placed in front.<br/><br/>(''The symbol'' ~ ''has many other uses, so'' ¬ '' or the slash notation is preferred. Computer scientists will often use'' ! ''but this is avoided in mathematical texts.'')
 | examples = ¬(¬''A'') ⇔ ''A'' <br/>''x'' ≠ ''y''  ⇔  ¬(''x'' =  ''y'')
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[Proportionality (mathematics)|∝]]}}
 | tex      = <math>\propto</math><br/>\propto
 | rowspan  = 1
 | name     = [[proportionality (mathematics)|proportionality]]
 | readas   = is proportional to;<br/>varies as
 | category = everywhere
 | explain  = ''y'' ∝ ''x'' means that ''y'' = ''kx'' for some constant ''k''.
 | examples = if ''y'' = 2''x'', then ''y'' ∝ ''x''.
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[Infinity|∞]]}}
 | tex      = <math>\infty</math><br/>\infty
 | rowspan  = 1
 | name     = [[infinity]]
 | readas   = infinity
 | category = [[number]]s
 | explain  = ∞ is an element of the [[extended real number line|extended number line]] that is greater than all real numbers; it often occurs in [[limit (mathematics)|limits]].
 | examples = <math>\lim_{x\to 0} \frac{1}{|x|} = \infty</math>
}}

{{row of table of mathematical symbols
 | symbol   = {{math|[[tombstone (typography)|■]]<br/><br/>□<br/><br/>∎<br/><br/>▮<br/><br/>‣}}
 | tex      = <math>\blacksquare</math><br/>\blacksquare<br/><br/><math>\Box</math><br/>\Box<br/><br/><math>\blacktriangleright</math><br>\blacktriangleright
 | rowspan  = 1
 | name     = [[end of proof]]
 | readas   = [[quod erat demonstrandum|QED]];<br/>[[tombstone (typography)|tombstone]];<br/>Halmos finality symbol
 | category = everywhere
 | explain  = Used to mark the end of a proof.<br/><br/>(''May also be written'' Q.E.D.)
| examples = 
}}

|}

==Symbols based on equality==
