    #域名www.domain1.tk绑定本地网址http://127.0.0.1:43111
    server {
        listen 443 ssl;
        listen [::]:443 ssl;
        server_name www.domain1.tk;
        access_log off;

        #以下为密码认证配置
        #用户密码文件htpasswd，放入配置文件所在的文件夹
        auth_basic "user and password !";
        auth_basic_user_file htpasswd;
        #以上为密码认证配置

