    __int64 sum[100000] = { 1, 1 };
    int math(int a)
    {
    	int j;
    	__int64 cache = 1;
    	for (j = 2; j <= a; j++)
    	{
    		cache = cache*j % 1000000;
    		sum[j] = (cache + sum[j - 1]) % 1000000;
    	}
    	return 0;
    }
    int main()
    {
    	int i;
    	while (~scanf("%d", &i;))
    	{
    		math(i);
