	],
	"topic_vote": {
		"1_1PowEPhsjjP1wFTYg3pEbaQHGXBUoWRczK": 1,
		"1_1JHrskNwA3oo9gxvCbo3Q1RZTUuKCDdfUa": 1,
		"2_1J3rJ8ecnwH2EPYa6MrgZttBNc61ACFiCj": 1,
