#!/usr/bin/env python3
"""
 E/I
 S/N
 T/F
 J/P

if J:
  t/f extraverted
  s/n is introverted
if P:
  s/n extraverted
  t/f is introverted

if E:
  extraverted is dominant
if I:
  introverted is dominant

3rd:
  opposite of aux, attitude of dominant
4th:
  opposite of dominant, opposite attitude
"""

def analyze(mbti):

    EI = mbti[0]
    SN = mbti[1]
    TF = mbti[2]
    JP = mbti[3]

    if JP == 'J':
        TF = TF+'e'
        SN = SN + 'i'
    elif JP == 'P':
        TF = TF + 'i'
        SN = SN + 'e'

    ALL = []

    if EI == 'E':
        if SN.endswith('e'):
            ALL.append(SN)
            ALL.append(TF)
        else:
            ALL.append(TF)
            ALL.append(SN)
    elif EI == 'I':
        if SN.endswith('i'):
            ALL.append(SN)
            ALL.append(TF)
        else:
            ALL.append(TF)
            ALL.append(SN)

    (DOM, AUX) = ALL

    XLATE = dict(S='N', N='S', T='F', F='T', e='i', i='e')

    F3 = XLATE[AUX[0]] + DOM[1]
    F4 = XLATE[DOM[0]] + XLATE[DOM[1]]

    ALL.append(F3)
    ALL.append(F4)

    return ' '.join(ALL)

for ei in 'EI':
    for sn in 'SN':
        for tf in 'TF':
            for jp in 'JP':
                mbti = ei+sn+tf+jp
                print('{0}\t{1}'.format(mbti, analyze(mbti)))

