# Garbage Code

import math

def isNum(s):
    try:
        int(s)
        return True
    except ValueError:
        return False

nums = []
f = open("output", "w")

print("Input a number or to quit, type in nothing")
while True:
    i = input()
    num:float = 0

    if isNum(i):
        num = float(i)
        nums.append(num)
    elif bool(i):
        for n in list(i):
            nums.append(ord(n))
    else:
        break

if not bool(nums):
    quit()

for n in nums:    
    f.write("\n" + str(int(n)) + "\n")
    binary = ""
    number = float(n)

    while True:
        number = math.floor(number)/float(2)
        f.write("\n" + str(number) + "\n")

        if number == int(0):
            break

        if number - round(number) == 0:
            binary = binary + "0"
        else:
            binary = binary + "1"

    f.write("\nOutput: " + binary[::-1] + "\n")
    f.write("Reality: " + str(bin(int(n))) + "\n")
    f.write("-----------------------------------\n")
