function interceptClickEvent(e) {
    var target = e.target || e.srcElement;
    if (target.tagName === 'A') {
        var is_proxy = (document.location.host == "zero" || window.location.pathname == "/")
        var href = target.getAttribute('href')
        if (is_proxy) {
            href = href.replace(/http:\/\/(127.0.0.1|localhost):43110/, 'http://zero')
            href = href.replace(/http:\/\/zero\/([^\/]+\.bit)/, "http://$1")
        } else {
            href = href.replace(/http:\/\/(127.0.0.1|localhost):43110/, '')
        }
        target.setAttribute("href", href)
    }
}
document.addEventListener('click', interceptClickEvent);
