public static int IntScanner() {
        Scanner sc = new Scanner(System.in);
        int num = 0;
        boolean check = false;
        do {
            try {
                num = sc.nextInt();
                check = true;
            } catch (Exception e) {
                System.out.println("Inserte un numero valido");
                check = false;
                sc.nextLine();
            }
        } while (!check);
        return num;
    }
