#include<cmath>
#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;
typedef long long ll;
inline const int Read()
{    int Num=0,Sgn=1;char x=getchar();
    while(!isdigit(x)){if(x=='-')Sgn=-1;x=getchar();}
    while(isdigit(x)){Num=(Num<<1)+(Num<<3)+x-'0';x=getchar();}
    return Num*Sgn;
}
int N,M,r[1000005],d,s,t;
int Tv[5000005],TAdd[5000005];
void Pushup(int x){Tv[x]=min(Tv[x<<1],Tv[x<<1|1]);}
void Pushdown(int x)
{    Tv[x]-=TAdd[x];
    TAdd[x<<1]+=TAdd[x];
    TAdd[x<<1|1]+=TAdd[x];
    TAdd[x]=0;
}
void Build(int L,int R,int v)
{    if(L==R){Tv[v]=r[L];return;}
    int Mid=(L+R)>>1;
    Build(L,Mid,v<<1);
    Build(Mid+1,R,v<<1|1);
    Pushup(v);
}
void Modify(int L,int R,int v)
{    Pushdown(v);
    if(L>t||R<s)return;
    if(L>=s&&R<=t)
    {    TAdd[v]+=d;
        Pushdown(v);
        return;
    }int Mid=(L+R)>>1;
    Modify(L,Mid,v<<1);
    Modify(Mid+1,R,v<<1|1);
    Pushup(v);
}
int main()
{    //freopen("classroom.in","r",stdin);
    //freopen("classroom.out","w",stdout);
    N=Read(); M=Read();
    for(int i=1;i<=N;i++)r[i]=Read();
    Build(1,N,1);
    for(int i=1;i<=M;i++)
    {    d=Read(),s=Read(),t=Read();
        Modify(1,N,1);
        if(Tv[1]<0){printf("-1\n%d",i);return 0;}
    }printf("0");
    //fclose(stdin);
    //fclose(stdout);
    return 0;
}
