#include<cmath>
#include<cstdio>
#include<vector>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;
int N,d,M;
char Str[50005];
int a,b;char Typea,Typeb;
struct Edge{int From,To;char now,next;};
vector<Edge>G[50005];
char Ans[50005];
void Addedge(int x,char a,int y,char b)
{    G[x].push_back((Edge){x,y,a,b});    }
bool Check(int x,char C)
{    for(int i=0;i<G[x].size();i++)
    {    Edge& e=G[x][i];
        if(e.now!=C)continue;
        if(Ans[e.To]&&Ans[e.To]!=e.next||Str[x]-'a'+'A'==e.next)return 0;
        if(!Check(e.To,e.next))return 0;
    }
    return 1;
}
void Set(int x,char C)
{    for(int i=0;i<G[x].size();i++)
    {    Edge& e=G[x][i];
        if(e.now!=C)continue;
        if(!Ans[e.To])Set(e.To,e.next);
        Ans[e.To]=e.next;
    }
}
void DFS(int x)
{    if(x>N)
    {    for(int i=1;i<=N;i++)putchar(Ans[i]);
        exit(0);
    }
    if(Ans[x])DFS(x+1);
    else
    {    for(char Now='A';Now<='C';Now++)
        {    if(Str[x]-'a'+'A'==Now)continue;
            if(!Check(x,Now))continue;
            Set(x,Now);
            Ans[x]=Now;
            DFS(x+1);
            Ans[x]='\0';    
        }
    }
}
int main()
{   scanf("%d%d",&N,&d);
    scanf("%s",Str+1);
    scanf("%d",&M);
    for(int i=1;i<=M;i++)scanf("%d %c %d %c",&a,&Typea,&b,&Typeb),Addedge(a,Typea,b,Typeb);
    DFS(1);
    return 0;
}​​​​​​​
