#读写/dev/cache需要root权限（并不是android的root权限）
user  root;
#工作进程数，手机常开几个核就写几
worker_processes  4;

#错误日志
error_log /home/logs/error.log warn;

#指定Nginx进程的PID
#pid logs/nginx.pid;

#每个进程的最大连接数
events {
	worker_connections  1024;
}


http {

	#etag是好东西，可惜不能和gzip一起用
	etag off;

	include       mime.types;
	default_type  application/octet-stream;

	proxy_connect_timeout 10;
	proxy_read_timeout 120;
	proxy_send_timeout 10;

	proxy_buffer_size 32k;
	proxy_buffers 4 64k;
	proxy_busy_buffers_size 128k;
	proxy_temp_file_write_size 128k;
	proxy_temp_path /home/temp_dir;

	#/dev是个内存盘，Gnuroot可以访问，所以就在里面做缓存
	proxy_cache_path /dev/cache levels=1:2 keys_zone=cache_one:8m inactive=12h max_size=80m;

	#访问日志，debug用
	#log_format main '"$upstream_cache_status" "$gzip_ratio"';
	#access_log  /home/logs/access.log  main;

	#no-push和no-delay互斥，ZeroNet请求不多，开tcp_nodelay就好
	sendfile on;
	tcp_nopush off;
	tcp_nodelay on;

	keepalive_timeout  60;

	#页面压缩
	gzip on;
	gzip_min_length 4k;
	gzip_buffers 8 32k;
	gzip_http_version 1.1;
	gzip_comp_level 2;
	gzip_types text/plain text/css application/javascript application/x-javascript text/javascript;
	gzip_vary on;
	gzip_proxied all;
	gzip_disable "MSIE [1-6]\.";

	server {

	#监听端口，在GNURoot中无法打开80端口
	listen       2080;
	server_name  localhost;

	location / {

		proxy_pass http://127.0.0.1:43110/;
		proxy_http_version 1.1;

		proxy_set_header X-Real-IP $remote_addr;
		proxy_set_header Host $host;
		proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
		proxy_set_header Upgrade $http_upgrade;
		proxy_set_header Connection "upgrade";

		proxy_read_timeout 900s;

		#忽略ZeroNet的缓存期限
		proxy_ignore_headers "Expires";
		proxy_ignore_headers "X-Accel-Expires";
		proxy_ignore_headers "Cache-Control";
		proxy_ignore_headers "Set-Cookie";

		#不向浏览器传送ZeroNet设置的的期限，建议和下面那个一起打开
		#proxy_hide_header "Expires";
		#proxy_hide_header "X-Accel-Expires";
		#proxy_hide_header "Cache-Control";
		#proxy_hide_header "Set-Cookie";

		#为浏览器的缓存重新设定期限，建议和上面那个一起打开
		#expires 1d;

		proxy_cache cache_one;
		proxy_cache_valid 1d;

		#返回缓存状态，debug用
		#add_header     Nginx-Cache   "$upstream_cache_status";

		}

	}

}
