apt-get install pip
pip install virtualenv
virtualenv -p /usr/bin/python zeronet
source /env/zeronet/bin/activate

wget [https://github.com/HelloZeroNet/ZeroNet/archive/v0.5.5.zip](https://github.com/HelloZeroNet/ZeroNet/archive/v0.5.5.zip)
unzip v0.5.5.zip
cd ZeroNet-0.5.5

cd plugins
mv disabled-Multiuser Multiuser
mv disabled-UiPassword UiPassword
cd ..

python zeronet.py
apt-get install nginx
cd /etc/nginx/sites-enabled

vi zeronet
```
server {
listen 80;
server_name z.ultragis.com;

    location / {
            proxy_pass http://127.0.0.1:43110;
    }
    location /Websocket {
            proxy_pass http://127.0.0.1:43110;

            proxy_http_version 1.1;
            proxy_set_header Upgrade $http_upgrade;
            proxy_set_header Connection "upgrade";
    }
}
```

nginx -s reload
